import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Product } from 'src/app/models/product';
import { ProductImage } from 'src/app/models/productImage';
import { Size } from 'src/app/models/size';
import { ProductImageService } from 'src/app/services/product-image.service';
import { ProductService } from 'src/app/services/product.service';

@Component({
  selector: 'app-product',
  templateUrl: './product.component.html',
  styleUrls: ['./product.component.css'],
})
export class ProductComponent implements OnInit {
  products: Product[] = [];
  productImage:ProductImage[];
  sizes:Size[];
  dataLoaded = false;
  currentImage:ProductImage
  Path = "https://localhost:44349"

  constructor(
    private productService: ProductService,
    private productImageService:ProductImageService,
    private activatedRoute: ActivatedRoute
  ) {}

  ngOnInit(): void {
   // this.activatedRoute.params.subscribe((params) => {
     // if (params["genderId"]) {
       // this.getProductsByGender(params["genderId"]);
     // } //else {
        //this.getProductDetails();
      //}
    //});

    this.getProductDetails();
             this.geProductImages()
  }

  getProducts() {
    this.productService.getProducts().subscribe((response) => {
      this.products = response.data;
      this.dataLoaded = true;
    });
  }

  getProductDetails() {
    this.productService.getProductDetails().subscribe((response) => {
      console.log(response);
      this.products = response.data;
      this.productImage=this.products[0].productImage
      this.dataLoaded = true;
    });
  }

  getProductsByGender(genderId:number) {
    this.productService.getProductsByGender(genderId).subscribe((response) => {
      this.products = response.data;
      this.dataLoaded = true;
    });
  }

  geProductImages(){
    this.productImageService.getImages().subscribe(response => {
      this.productImage=response.data
      this.dataLoaded=true
    })
  }

  getProductImagesByProductId(carId: number) {
    this.productImageService.getImagesByCarId(carId).subscribe((response) => {
      this.productImage = response.data;
      this.dataLoaded = true;
    });
  }

  getCurrentImageClass(image: ProductImage) {
    if (image == this.productImage[0]) {
      return 'carousel-item active';
    } else {
      return 'carousel-item';
    }
  }
}
