import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Brand } from 'src/app/models/brand';
import { Category } from 'src/app/models/category';
import { Color } from 'src/app/models/color';
import { Product } from 'src/app/models/product';
import { ProductDetail } from 'src/app/models/productDetail';
import { ProductImage } from 'src/app/models/productImage';
import { Size } from 'src/app/models/size';
import { ProductService } from 'src/app/services/product.service';

@Component({
  selector: 'app-product',
  templateUrl: './product.component.html',
  styleUrls: ['./product.component.css'],
})
export class ProductComponent implements OnInit {
  products: Product[] = [];
  productDetails:ProductDetail[]=[];
  productImage:ProductImage[];
  categories:Category[]=[];
  brands:Brand[]=[];
  colors:Color[]=[];
  sizes:Size[];
  dataLoaded = false;
  currentImage:ProductImage


  constructor(
    private productService: ProductService,
    private activatedRoute: ActivatedRoute
  ) {}

  ngOnInit(): void {

    this.activatedRoute.params.subscribe((params) => {
     if (params["genderId"]) {
       this.getProductsByGender(params["genderId"]);
      } else if (params["brandId"] || params["colorId"] || params["categoryId"]) {
        this.getProductsByFilter();
      } else {
        this.getProductDetails();
      }
    });
  }

  getProductDetails() {
    this.productService.getProductDetails().subscribe((response) => {

      this.productDetails = response.data;

      this.dataLoaded = true;
      console.log(this.productDetails);
    });
  }

  getProductsByGender(genderId:number) {
    this.productService.getProductsByGender(genderId).subscribe((response) => {
      this.products = response.data;
      this.dataLoaded = true;
    });
  }

  getProductsByFilter() {
    this.activatedRoute.params.subscribe((params) => {
      const brandId = +params['brandId'];  
      const colorId = +params['colorId'];
      const genderId = +params['genderId'];
      const categoryId = +params['categoryId'];

      this.productService.getProductDetailsFilter(colorId, brandId, categoryId, genderId)
        .subscribe(response => {
          this.productDetails = response.data.flat();
          this.dataLoaded = true;
        });
    });
  }
}
