import { Injectable } from '@angular/core';
import { Product } from '../models/product';
import { ListResponseModel } from '../models/listResponseModel';
import { Observable } from 'rxjs';
import { HttpClient, HttpParams } from '@angular/common/http';
import { ProductDetail } from '../models/productDetail';

@Injectable({
  providedIn: 'root'
})
export class ProductService {

  apiUrl = 'https://localhost:44383/api/';
  constructor(private httpClient: HttpClient) { }

  getProducts():Observable<ListResponseModel<Product>> {
    let newPath =this.apiUrl +"Products/getall"
    return this.httpClient.get<ListResponseModel<Product>>(newPath);
   }

   getProductDetails():Observable<ListResponseModel<ProductDetail>> {
    let newPath =this.apiUrl +"Products/details"
    return this.httpClient.get<ListResponseModel<ProductDetail>>(newPath);
   }

   getProductsByGender(genderId:number):Observable<ListResponseModel<Product>> {
    let newPath =this.apiUrl +"Products/getbygenderid?genderId="+genderId
    return this.httpClient.get<ListResponseModel<Product>>(newPath);
   }

   getProductById(productId:number):Observable<ListResponseModel<Product>>{
    let newPath=this.apiUrl+"Products/getProductDetailsById?productId="+ productId
    return this.httpClient.get<ListResponseModel<Product>>(newPath)
  }

  getProductDetailsFilter(
    colorId?: number,
    brandId?: number,
    categoryId?: number,
    genderId?: number
  ): Observable<ListResponseModel<ProductDetail[]>> {
    let params = new HttpParams();
    if (colorId) params = params.append('colorId', colorId.toString());
    if (brandId) params = params.append('brandId', brandId.toString());
    if (categoryId) params = params.append('categoryId', categoryId.toString());
    if (genderId) params = params.append('genderId', genderId.toString());

    let newPath = this.apiUrl + "Products/getProductDetailsFilter";
    return this.httpClient.get<ListResponseModel<ProductDetail[]>>(newPath, {params});
  }
}
